using System.Collections;
using System.Collections.Generic;
using UnityEngine;
using UnityEngine.Networking;
public class BounceQuaGiant : NetworkBehaviour {
    private Vector3 targetScale;
    private Vector3 startScale;
    private float startTime;
    private float existTime;
    private float speed;
    private void Start() {
        targetScale = Vector3.one * 20;
        startScale = Vector3.one;
        startTime = Time.time;
        existTime = 0.7f;
        speed = (targetScale.x - startScale.x) / existTime;
    }
    void Update() {
        //if (!isServer)//实际上不同步- -
        //    return;
        transform.localScale = startScale * (1 + (Time.time - startTime) * speed);
        if (Time.time - startTime > existTime) {
            if (isServer) {
                NetObjectId.Objects.Remove(gameObject);
            }
            Destroy(gameObject);
        }
    }
}
